mapper.select:
select id,goods_name,category_id,sub_category_id,price,images,detail,inventory,bargain,view_num,collect_num,hot_num,owner_id,status,created,updated
from 

    owner_id = #{ownerId}

        and goods_name like "%"#{search}"%"


         and status = #{status}
         and status > 0



    order by created ${order}

limit #{offset},#{limit}

select count(1)
from 

    owner_id = #{ownerId}

        and goods_name like "%"#{search}"%"


         and status = #{status}
         and status > 0

select id,goods_name,category_id,sub_category_id,price,images,created
from 


        category_id = #{categoryId}


        and sub_category_id = #{subCategoryId}

    and status=1

order by created DESC
limit #{maxSize}

SELECT id,goods_name,category_id,sub_category_id,price,images,created,IFNULL(comment_count.count, 0) * 5 + hot_num * 2 + view_num AS score FROM goods LEFT JOIN ( SELECT entity_id, count(1) AS count FROM COMMENT WHERE entity_type = 1 GROUP BY entity_id ) AS comment_count ON goods.id = comment_count.entity_id
category_id = #{categoryId}
and sub_category_id=#{subCategoryId}
and status=1
ORDER BY score desc limit #{maxSize}
